<?phptry{    header("Content-Type: text/html;charset=utf-8");        require('../DB.php');     //    $dbh = null; don't forget to close the connection!	//Getting records (listAction)	if($_GET["action"] == "list"){			//Get records from database			if(!$result = ("SELECT * FROM calificaciones WHERE id_ca_alumnos = " . $_GET["id_alumnos"] . "; ")){			throw new Exception('Query failed: '. mysql_error($con));		}		//Add all records to an array		$rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }		//Return result to jTable		$jTableResult = array();		$jTableResult['Result'] = "OK";		$jTableResult['Records'] = $rows;		print json_encode($jTableResult);	}	//Creating a new record (createAction)		else if($_GET["action"] == "create"){		//Insert record into database		if(!$result = $dbh->prepare ("INSERT INTO calificaciones(calificacion, impartida_por, promedio_anual,		unidades_segun_grado, id_ca_cursos, id_ca_unidades, id_ca_alumnos, id_ca_grados, id_ca_secciones, id_ca_carreras)		VALUES('" . $_POST["calificacion"] . "','" . $_POST["impartida_por"] ."','" . $_POST["promedio_anual"] . 		"','" . $_POST["unidades_segun_grado"] ."'," . $_POST["id_ca_cursos"] . ",	" . $_POST["id_ca_unidades"] . "," . $_POST["id_ca_alumnos"] . 		"," . $_POST["id_ca_grados"] . "," . $_POST["id_ca_secciones"] . "," . $_POST["id_ca_carreras"] . " );")){			throw new Exception('Query failed: '. mysql_error($con));					}        $result->execute();				//Get last inserted record (to return to jTable)		$result = $dbh->prepare("SELECT * FROM calificaciones WHERE id_calificaciones = LAST_INSERT_ID();");        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);		//Return result to jTable		$jTableResult = array();		$jTableResult['Result'] = "OK";		$jTableResult['Record'] = $row;		print json_encode($jTableResult);    }	//Updating a record (updateAction)	else if($_GET["action"] == "update"){		//Update record in database		if(!$result = $dbh->prepare("UPDATE calificaciones SET calificacion = " . $_POST["calificacion"] . ", impartida_por = '" . $_POST["impartida_por"] . "', 		promedio_anual = " . $_POST["promedio_anual"] . ", 		unidades_segun_grado = " . $_POST["unidades_segun_grado"] . ", id_ca_cursos = " . $_POST["id_ca_cursos"] . ", 		id_ca_unidades = " . $_POST["id_ca_unidades"] . ", id_ca_alumnos = " . $_POST["id_ca_alumnos"] . ", 		id_ca_grados = " . $_POST["id_ca_grados"] . ", id_ca_secciones = " . $_POST["id_ca_secciones"] . ", 		id_ca_carreras = " . $_POST["id_ca_carreras"] . " WHERE id_calificaciones = " . $_POST["id_calificaciones"]. ";")){			throw new Exception('Query failed: '. mysql_error($con));								}        $result->execute();		//Return result to jTable		$jTableResult = array();		$jTableResult['Result'] = "OK";		print json_encode($jTableResult);	}	//Deleting a record (deleteAction)	else if($_GET["action"] == "delete"){		if(!$result = $dbh->prepare("DELETE FROM calificaciones WHERE id_calificaciones = " . $_POST["id_calificaciones"] ." ; ")){			throw new Exception('Query failed: '. mysql_error($con));								}        $result->execute();		//Return result to jTable		$jTableResult = array();		$jTableResult['Result'] = "OK";		print json_encode($jTableResult);	}    /* extrae alumnos por sección para asignar notas*/    if($_GET["action"] == "secciones"){        $id_secciones = $_GET['id_secciones'];		//Get records from database		if(!$result = $dbh->prepare("SELECT COUNT(*) AS Registros FROM alumnos;")){			throw new Exception('Query failed: '. mysql_error($con));		}        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);		$registros = $row['Registros'];              //Get records from database        if(!$result = ("SELECT id_alumnos, id_a_grados, id_a_secciones, id_a_carreras, nombres_alumnos, apellidos_alumnos            FROM alumnos WHERE id_a_secciones = " .$id_secciones."             ORDER BY " . $_GET["jtSorting"] . " LIMIT " . $_GET["jtStartIndex"] . "," . $_GET["jtPageSize"] . ";")){            throw new Exception('Query failed: '. mysql_error($con));        }				//Add all records to an array		$rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }		//Return result to jTable		$jTableResult = array();		$jTableResult['Result'] = "OK";		$jTableResult['TotalRecordCount'] = $registros;		$jTableResult['Records'] = $rows;		print json_encode($jTableResult);	}    /* close the database connection */        $dbh = null;    if ($dbh) {        var_dump($dbh, " connection still active");        }    } catch (Exception $ex) {    $ex->getMessage();    print $ex;    //Return error message	$jTableResult = array();	$jTableResult['Result'] = "ERROR";	$jTableResult['Message'] = $ex->getMessage();	print json_encode($jTableResult);}	?>