<?phptry {    header("Content-Type: text/html;charset=utf-8");    require('../DB.php');     //    $dbh = null; don't forget to close the connection!    //Getting records (listAction)    if ($_GET["action"] == "list") {        if (!$result = $dbh->prepare("SELECT COUNT(*) AS Registros FROM carreras")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        $registros = $row['Registros'];        //Get records from database        if (!$result = ("SELECT * FROM carreras            ORDER BY " . $_GET["jtSorting"] . " LIMIT " . $_GET["jtStartIndex"] . "," . $_GET["jtPageSize"] . "")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        //Add all records to an array        $rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['TotalRecordCount'] = $registros;        $jTableResult['Records'] = $rows;        print json_encode($jTableResult);    }    //Creating a new record (createAction)    else if ($_GET["action"] == "create") {        if (!$result = $dbh->prepare("INSERT INTO carreras(nombre_carrera, ciclos_d_duracion, codigo_de_carrera) VALUES		('" . $_POST["nombre_carrera"] . "', '" . $_POST["ciclos_d_duracion"] . "', '" . $_POST["codigo_de_carrera"] . "' );")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Get last inserted record (to return to jTable)        $result = $dbh->prepare("SELECT * FROM carreras WHERE id_carreras = LAST_INSERT_ID();");        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Record'] = $row;        print json_encode($jTableResult);    }    //Updating a record (updateAction)    else if ($_GET["action"] == "update") {        if (!$result = $dbh->prepare("UPDATE carreras SET nombre_carrera = '" . $_POST["nombre_carrera"] .            "', ciclos_d_duracion = '" . $_POST["ciclos_d_duracion"] . "', codigo_de_carrera = '" . $_POST["codigo_de_carrera"] .            "' WHERE id_carreras = " . $_POST["id_carreras"] . ";")){            throw new Exception('Query failed: ' . mysql_error($dbh));                    }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    //Deleting a record (deleteAction)    else if ($_GET["action"] == "delete") {        if (!$result = $dbh->prepare("DELETE FROM carreras             WHERE id_carreras = " . $_POST["id_carreras"] . " ;")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    else if ($_GET["action"] == "carrera") {        $rows = array();        if (!$result = ("SELECT nombre_carrera AS DisplayText, id_carreras AS Value FROM carreras ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));                                                                              }        //Add all records to an array        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }                //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Options'] = $rows;        print json_encode($jTableResult);    }        /* close the database connection */    $dbh = null;    if ($dbh) {        var_dump($dbh, " connection still active");    }} catch (Exception $ex) {    $ex->getMessage();    print $ex;    //Return error message    $jTableResult = array();    $jTableResult['Result'] = "ERROR";    $jTableResult['Message'] = $ex->getMessage();    print json_encode($jTableResult);}?>