<?php	try{	//Open database connection	$con = mysql_connect("localhost","root","");	mysql_select_db("studentadmindb", $con);		$query = "SELECT id_seccions FROM alumnos";		$result = mysql_query ($query);	echo "<select name=student value=''>Student Name</option>";	// printing the list box select command	while($nt=mysql_fetch_array($result)){//Array or records stored in $nt	echo "<option value=$nt[id]>$nt[name]</option>";	/* Option values are added by looping through the array */	}	echo "</select>";// Closing of list box 		//Close database connection	mysql_close($con);	}	catch(Exception $ex){	}	?>