<?phptry {    header("Content-Type: text/html;charset=utf-8");    require('../DB.php');     //    $dbh = null; don't forget to close the connection!    //Getting records (listAction)    if ($_GET["action"] == "list") {        if (!$result = ("SELECT * FROM secciones WHERE id_s_grados =  " . $_GET["id_grados"] . " ;")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        //Add all records to an array        $rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Records'] = $rows;        print json_encode($jTableResult);    }    //Creating a new record (createAction)	    else if ($_GET["action"] == "create") {        if (!$result = $dbh->prepare("INSERT INTO secciones(id_s_grados, id_s_carreras, nombre_d_seccion, maestro_guia)		VALUES(" . $_POST["id_s_grados"] . "," . $_POST["id_s_carreras"] . ", '" . $_POST["nombre_d_seccion"] . "',		'" . $_POST["maestro_guia"] . "' );")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Get last inserted record (to return to jTable)        $result = $dbh->prepare("SELECT * FROM secciones WHERE id_secciones = LAST_INSERT_ID();");        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Record'] = $row;        print json_encode($jTableResult);    }    //Updating a record (updateAction)    else if ($_GET["action"] == "update") {        if (!$result = $dbh->prepare("UPDATE secciones SET nombre_d_seccion = '" . $_POST["nombre_d_seccion"] .            "', maestro_guia = '" . $_POST["maestro_guia"] . "' WHERE id_secciones = " . $_POST["id_secciones"] . ";")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    //Deleting a record (deleteAction)    else if ($_GET["action"] == "delete") {        if (!$result = $dbh->prepare("DELETE FROM secciones WHERE id_secciones = " . $_POST["id_secciones"] . " ; ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    else if ($_GET["action"] == "seccion") {        $rows = array();        if (!$result = ("SELECT nombre_d_seccion AS DisplayText, id_secciones AS Value FROM secciones ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));                                                                              }        //Add all records to an array        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }                //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Options'] = $rows;        print json_encode($jTableResult);    }        /* close the database connection */    $dbh = null;    if ($dbh) {        var_dump($dbh, " connection still active");    }} catch (Exception $ex) {    $ex->getMessage();    print $ex;    //Return error message    $jTableResult = array();    $jTableResult['Result'] = "ERROR";    $jTableResult['Message'] = $ex->getMessage();    print json_encode($jTableResult);}?>