<?phptry {    header("Content-Type: text/html;charset=utf-8");    require('../DB.php');     //    $dbh = null; don't forget to close the connection!    //Getting records (listAction)    if ($_GET["action"] == "list") {        if (!$result = $dbh->prepare("SELECT COUNT(*) AS Registros FROM unidades;")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        $registros = $row['Registros'];        //Get records from database        if (!$result = ("SELECT * FROM unidades            ORDER BY " . $_GET["jtSorting"] . " LIMIT " . $_GET["jtStartIndex"] . "," . $_GET["jtPageSize"] . "")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        //Add all records to an array        $rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['TotalRecordCount'] = $registros;        $jTableResult['Records'] = $rows;        print json_encode($jTableResult);    }    //Creating a new record (createAction)	    else if ($_GET["action"] == "create") {        if (!$result = $dbh->prepare("INSERT INTO unidades(unidades_por_ciclo, nombre_de_unidad, ciclo)		VALUES('" . $_POST["unidades_por_ciclo"] . "','" . $_POST["nombre_de_unidad"] . "','" . $_POST["ciclo"] . "' );")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Get last inserted record (to return to jTable)        $result = $dbh->prepare("SELECT * FROM unidades WHERE id_unidades = LAST_INSERT_ID();");        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Record'] = $row;        print json_encode($jTableResult);    }        //Updating a record (updateAction)    else if ($_GET["action"] == "update") {        if (!$result = $dbh->prepare("UPDATE unidades SET unidades_por_ciclo = '" . $_POST["unidades_por_ciclo"] .            "', nombre_de_unidad = '" . $_POST["nombre_de_unidad"] . "', ciclo = '" . $_POST["ciclo"] .            "' WHERE id_unidades = " . $_POST["id_unidades"] . ";")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    //Deleting a record (deleteAction)    else if ($_GET["action"] == "delete") {        if (!$result = $dbh->prepare("DELETE FROM unidades WHERE id_unidades = " . $_POST["id_unidades"] . " ; ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    /* close the database connection */    $dbh = null;    if ($dbh) {        var_dump($dbh, " connection still active");    }} catch (Exception $ex) {    $ex->getMessage();    print $ex;    //Return error message    $jTableResult = array();    $jTableResult['Result'] = "ERROR";    $jTableResult['Message'] = $ex->getMessage();    print json_encode($jTableResult);}?>