<?phptry {    header("Content-Type: text/html;charset=utf-8");    require('../DB.php');     //    $dbh = null; don't forget to close the connection!    //Getting records (listAction)    if ($_GET["action"] == "list") {        if (!$result = ("SELECT * FROM grados WHERE id_g_carreras = " . $_GET["id_carreras"] . " ;")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        //Add all records to an array        $rows = array();        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Records'] = $rows;        print json_encode($jTableResult);    }    //Creating a new record (createAction)	    else if ($_GET["action"] == "create") {        //Insert record into database        if (!$result = $dbh->prepare("INSERT INTO grados(id_g_carreras, grado, aula, maestro_coordinador) VALUES		(" . $_POST["id_g_carreras"] . ",'" . $_POST["grado"] . "', '" . $_POST["aula"] . "',		'" . $_POST["maestro_coordinador"] . "' );")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Get last inserted record (to return to jTable)        $result = $dbh->prepare("SELECT * FROM grados WHERE id_grados = LAST_INSERT_ID();");        $result->execute();        $row = $result->fetch(PDO::FETCH_ASSOC);        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Record'] = $row;        print json_encode($jTableResult);    }    //Updating a record (updateAction)    else if ($_GET["action"] == "update") {        //Update record in database        if (!$result = $dbh->prepare("UPDATE grados SET grado = '" . $_POST["grado"] .            "', aula = '" . $_POST["aula"] . "', maestro_coordinador = '" . $_POST["maestro_coordinador"] .            "' WHERE id_grados = " . $_POST["id_grados"] . ";")){            throw new Exception('Query failed: ' . mysql_error($dbh));                    }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    //Deleting a record (deleteAction)    else if ($_GET["action"] == "delete") {        //Delete from database        if (!$result = $dbh->prepare("DELETE FROM grados WHERE id_grados = " . $_POST["id_grados"] . " ; ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));        }        $result->execute();        //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        print json_encode($jTableResult);    }    else if ($_GET["action"] == "grado") {        $rows = array();        if (!$result = ("SELECT grado AS DisplayText, id_grados AS Value FROM grados ")) {            throw new Exception('Query failed: ' . mysql_error($dbh));                                                                              }        //Add all records to an array        foreach ($dbh->query($result) as $row) {            $rows[] = $row;        }                //Return result to jTable        $jTableResult = array();        $jTableResult['Result'] = "OK";        $jTableResult['Options'] = $rows;        print json_encode($jTableResult);    }        /* close the database connection */    $dbh = null;    if ($dbh) {        var_dump($dbh, " connection still active");    }} catch (Exception $ex) {    $ex->getMessage();    print $ex;    //Return error message    $jTableResult = array();    $jTableResult['Result'] = "ERROR";    $jTableResult['Message'] = $ex->getMessage();    print json_encode($jTableResult);}?>