_unidades: {                    title: 'Id de la Unidad',                    width: '18%',   		            key: true,       		        create: false,           		    edit: false,               		list: true               	},	               	               	               	                              	  				unidades_por_ciclo: {	   	           	title: 'Unidades por ciclo',  					create: true,  					edit: true,  					list: true,           	    	width: '20%'               	},               	nombre_de_unidad: {	           		title: 'Unidades',  					create: true,  					edit: true,  					list: true,              		width: '50%'              	},               	ciclo: {	           		title: 'Ciclo',  					create: true,  					edit: true,  					list: true,              		width: '10%'              	},	       	},			selectionChanged: function(){        		var $selectedRows = $('.UnidadesTableContainer').jtable('selectedRows');        		$('#SelectedRowList').empty();        		if($selectedRows.length > 0){        			$selectedRows.each(function(){        				var record = $(this).data('record');        				$('#SelectedRowList').append(        					'id_unidades:' + record.id_unidades + 'nombre_de_unidad:' + record.nombre_de_unidad        				);        			});        		}else{        			$('#SelectedRowList').append('No row selected!');        		}        	}		});		$('.UnidadesTableContainer').jtable('load');   				});    $(document).ready(function(){    $('table.jtable').css({'width':'700px', 'position':'relative', 'left':'145px'});    $('.jtable-title').css({'width':'698px', 'position':'relative', 'left':'143px'});    $('.jtable-bottom-panel').css({'width':'698px', 'position':'relative', 'left':'145px'});    //$('table.jtable').offset({top: 10, left: 30});    });    </script>	    </div>    <div id="footer_container"></div>            	</body></html>